--PharmCat 2.2 ZOMBIESSELECTMED_MNEMONIC,RXCUI,RXNORM_DESCRIPTION,ref.Concept_Status_Desc AS RXNORM_STATUS,refrelation.Relationship_Mnem_CS AS RELATIONSHIP,refrelation.Related_Source_Code AS REMAPPED_RXCUI,refrelation.Related_Source_Desc AS REMAPPED_DESCRIPTIONFROM `hca-usr-gbc-gme-prod.kva6331.SYMEDTABLE` medtableLEFT JOIN hca-hin-prod-cur-clinical.edwcl_views.ref_rxnorm refON medtable.RXCUI = CAST(ref.RxNorm_Id AS STRING)LEFT JOIN hca-hin-prod-cur-clinical.edwcl_views.ref_rxnorm_relationship refrelationON medtable.RXCUI = CAST(refrelation.RxNorm_Id AS STRING)WHERE refrelation.Relationship_Mnem_CS IN('HAS_QUAN_FORM','HAS_GENERIC_DRUG','HAS_INGREDIENT','RXN_CONTAINS','RXN_HAS_BOSS','HASPARENT') AND medtable.MED_MNEMONIC IN('REPLESTA50000 UNIT','MILK THISTLE150 MG','CRANBERRY250 MG','COMBIVENT INH14.7 GM','HEPARIN LO10 UNIT/3','GINKGO BILOBA60 MG','BLACK COHOSH160 MG','GARLIC500 M1','CRANBERRY300 MG','FISH OIL999 MG','FISH OIL 1,2001 EAC1','DIVIGEL30 GM','MILK THISTLE175 M1','GARLIC300 MG','IRON325 ( 65 )','AZO CRANBERRY1 EACH','HALOPERIDOL5 MG/1 M2')